import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";
import { Badge } from "@/components/ui/badge";
import { cn } from "@/lib/utils";

const statusVariants = cva("border-transparent hover:opacity-100", {
  variants: {
    status: {
      online: "bg-green-500 text-white",
      offline: "bg-gray-500 text-white",
      away: "bg-yellow-500 text-white",
      busy: "bg-red-500 text-white",
      idle: "bg-blue-500 text-white",
      pending: "bg-orange-500 text-white",
      success: "bg-green-600 text-white",
      error: "bg-red-600 text-white",
      warning: "bg-amber-500 text-white",
      info: "bg-blue-600 text-white"
    }
  },
  defaultVariants: {
    status: "offline"
  }
});

export interface StatusProps extends Omit<React.HTMLAttributes<HTMLDivElement>, "children">,
  VariantProps<typeof statusVariants> {
  status?: "online" | "offline" | "away" | "busy" | "idle" | "pending" | "success" | "error" | "warning" | "info";
  statusindicator?: boolean;
  indicatorClassName?: string;
  shiny?: boolean;
  shinySpeed?: number;
  children?: React.ReactNode;
}

const Status = React.forwardRef<HTMLDivElement, StatusProps>(
  ({ className, status = "offline", statusindicator = false, indicatorClassName, shiny = false, shinySpeed, children, ...props }, ref) => {
    return (
      <div ref={ref}>
        <Badge
          variant="outline"
          className={cn(statusVariants({ status }), className)}
          shiny={shiny}
          shinySpeed={shinySpeed}
          {...props}
        >
          <span className="inline-flex items-center gap-2">
            {statusindicator && (
              <span
                className={cn("flex-shrink-0 rounded-full bg-white", indicatorClassName)}
                style={{
                  width: indicatorClassName?.includes("w-") ? undefined : "0.375rem",
                  height: indicatorClassName?.includes("h-") ? undefined : "0.375rem"
                }}
              />
            )}
            {children}
          </span>
        </Badge>
      </div>
    );
  }
);

Status.displayName = "Status";

export { Status, statusVariants };

import * as React from "react";
import { cva, type VariantProps } from "class-variance-authority";
import { cn } from "@/lib/utils";

const badgeVariants = cva(
  "inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground hover:bg-primary/80",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive:
          "border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80",
        outline: "text-foreground"
      },
      shiny: {
        true: "relative overflow-hidden",
        false: ""
      }
    },
    defaultVariants: {
      variant: "default",
      shiny: false
    }
  }
);

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {
  shiny?: boolean;
  shinySpeed?: number;
}

function Badge({
  className,
  variant,
  shiny = false,
  shinySpeed = 5,
  children,
  ...props
}: BadgeProps) {
  const animationDuration = `${shinySpeed}s`;

  return (
    <div
      className={cn(badgeVariants({ variant, shiny }), className)}
      {...props}
    >
      <span className={shiny ? "relative z-10" : ""}>{children}</span>

      {shiny && (
        <span
          className="absolute inset-0 pointer-events-none animate-shine dark:hidden"
          style={{
            background:
              "linear-gradient(120deg, transparent 40%, rgba(255,255,255,0.6) 50%, transparent 60%)",
            backgroundSize: "200% 100%",
            animationDuration,
            mixBlendMode: "screen"
          }}
        />
      )}

      {shiny && (
        <span
          className="absolute inset-0 pointer-events-none animate-shine hidden dark:block"
          style={{
            background:
              "linear-gradient(120deg, transparent 40%, rgba(0,0,150,0.25) 50%, transparent 60%)",
            backgroundSize: "200% 100%",
            animationDuration,
            mixBlendMode: "multiply"
          }}
        />
      )}
    </div>
  );
}

export { Badge, badgeVariants };

import { clsx, type ClassValue } from "clsx";
import { twMerge } from "tailwind-merge";

export function cn(...inputs: ClassValue[]) {
  return twMerge(clsx(inputs));
}
